objects.pattern:
rule DAMASCENED_PEACOCK_API_hashes {
    meta:
        author = "NCSC"
        description = "This rule targets code patterns for pushing the
hash onto the stack and then moving the returned pointer into a structure
in DAMASCENED PEACOCK."
        hash1 = "8c25b982e5e4b3418b779c85b8ac8a6acf393063"

    strings:
        $ = {68 1A 81 A8 01} // push hash
        $ = {83 C4 04 89 46 04} // move result into struct

        $ = {68 7D 53 AE 52}
        $ = {83 C4 04 89 46 08}

        $ = {68 59 6F 3C 00}
        $ = {83 C4 04 89 46 0C}

        $ = {68 6B 9A C6 E1}
        $ = {83 C4 04 89 46 10}

        $ = {68 32 A9 7C 08}
        $ = {83 C4 04 89 46 14}

        $ = {68 76 EE 30 8A}
        $ = {83 C4 04 89 46 18}

    condition:
        (uint16(0) == 0x5A4D) and uint32(uint32(0x3C)) == 0x00004550 and
all of them

}
